import React, { useState } from "react";
import { View, Text, TouchableOpacity, Image, StyleSheet } from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { db } from "./config";

export default function MyCredit() {
  const [text1, setText1] = useState("");
  const [text2, setText2] = useState("");

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton}>
          <Icon name="arrow-left" size={20} color="black" />
        </TouchableOpacity>
        <Text style={styles.headerText}>My Credits</Text>
      </View>
      <View style={styles.marginContainer}>
        <View style={styles.rectangle}>
          <View style={styles.creditHeader}>
            <Image
              source={require("../assets/coins.png")} // Provide the path to your credit icon image
              style={styles.creditIcon}
            />
            <View style={styles.creditInfo}>
              <Text style={styles.creditText}>Credit Amount</Text>
              <Text style={styles.creditValue}>$100.00</Text>
              <Text style={styles.lastEditedDate}>Last Edited: 10/10/2023</Text>
            </View>
          </View>
          <TouchableOpacity style={styles.topUpButton}>
            <Text style={styles.topUpText}>Top Up Credits</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.creditHistory}>
          {/* List of credit history entries */}
          <Text style={styles.historyEntry}>200 credits - 10/10/2023</Text>
          {/* Add more entries as needed */}
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginTop: 40,
    flex: 1,
  },
  marginContainer: {
    marginHorizontal: 20,
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 20,
  },
  backButton: {
    width: 45,
    height: 45,
    borderRadius: 25,
    backgroundColor: "#000", // Black background color
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontSize: 24,
    fontWeight: "500",
    marginLeft: 10,
  },
  rectangle: {
    padding: 20,
    backgroundColor: "white", // Adjust as needed
