    borderColor: "#E2E0E0",
  },
  creditHeader: {
    flexDirection: "row",
    alignItems: "center",
  },
  creditIcon: {
    width: 30, // Adjust the width and height as needed
    height: 30,
  },
  creditInfo: {
    marginLeft: 10,
  },
  creditText: {
    fontSize: 18,
  },
  creditValue: {
    fontSize: 24,
    fontWeight: "500",
  },
  lastEditedDate: {
    fontSize: 14,
    color: "lightgray",
  },
  topUpButton: {
    backgroundColor: "blue", // Adjust the button styles
    padding: 10,
    alignItems: "center",
    marginTop: 20,
  },
  topUpText: {
    color: "white",
